import React from "react";
import BaseLayout from "../components/layouts/BaseLayout";
import { Container, Row, Col } from "reactstrap";
import Menu from "../components/shared/Menu";
import Link from "next/link";

const BLOG_POSTS_PATH = "../../content/blogs";

const importBlogPosts = async () => {
  const markdownFiles = require
    .context("../content/blogs", false, /\.md$/)
    .keys()
    .map(relativePath => relativePath.substring(2));
  return Promise.all(
    markdownFiles.map(async path => {
      const markdown = await import(`../content/blogs/${path}`);
      return { ...markdown, slug: path.substring(0, path.length - 3) };
    })
  );
};

class Blog extends React.Component {
  static async getInitialProps() {
    const postsList = await importBlogPosts();

    return { postsList };
  }
  render() {
    const { postsList } = this.props;
    return (
      <BaseLayout>
        <div className="blog-menu">
          <Container>
            <Row>
              <Col sm="12">
                <Menu />
              </Col>
            </Row>
          </Container>
        </div>
        <section className="p-50">
          <Container>
            <Row>
              <Col sm="12">
                <h2 className="ml-30">Blog</h2>
              </Col>
            </Row>
          </Container>
        </section>
        <Container>
          <Row>
            <Col md="8">
              <ul className="blog-post-list-bg">
                {postsList.slice(1).map(post => {
                  return (
                    <li>
                      <div className="post-wrapper">
                        <Link href={`blog/${post.slug}`}>
                          <a
                            style={{
                              backgroundImage: `url(${post.attributes.thumbnail})`
                            }}
                          >
                            <div className="post-box">
                              <h5>
                                Published at <span>{post.attributes.date}</span>{" "}
                                in <span>{post.attributes.category}</span>
                              </h5>
                              <h2>{post.attributes.title}</h2>
                            </div>
                          </a>
                        </Link>
                      </div>
                    </li>
                  );
                })}
              </ul>
            </Col>
            <Col md="4">
              <ul className="blog-post-list">
                {postsList.map(post => {
                  return (
                    <li>
                      <div className="post-wrapper">
                        <Link href={`blog/${post.slug}`}>
                          <a
                            style={{
                              backgroundImage: `url(${post.attributes.thumbnail})`
                            }}
                          >
                            <div className="post-box">
                              <h5>
                                Published at <span>{post.attributes.date}</span>{" "}
                                in <span>{post.attributes.category}</span>
                              </h5>
                              <h2>{post.attributes.title}</h2>
                            </div>
                          </a>
                        </Link>
                      </div>
                    </li>
                  );
                })}
              </ul>
            </Col>
          </Row>
        </Container>
      </BaseLayout>
    );
  }
}

export default Blog;
